                $departments = array_merge( $departments, array_keys( ph_get_custom_departments( true ) ) );

                $data_fields['meta']['_department'] = array(
                    'possible_values' => $departments,
                );

                $data_fields['meta'] = array_merge( $data_fields['meta'],
                    array(
                        '_on_market' => array(
