                            'field_bounds' => array('min' => 200, 'max' => 10000, 'round' => -2),
                            'dependent_field' => '_department',
                            'dependent_values' => array('residential-lettings'),
                        ),
                        '_rent_frequency' => array(
                            'possible_values' => array('pppw', 'pw', 'pcm', 'pq', 'pa'),
                            'dependent_field' => '_department',
                            'dependent_values' => array('residential-lettings'),
